from odoo import api, modelsfrom odoo.exceptions import ValidationErrorclass ProductPricelist(models.Model):    _inherit = 'product.pricelist'    @api.constrains('item_ids')    def constrains_item_ids(self):        list_line_pricelist = []        for x in self:            for line in x.item_ids:                # if x.id.origin or x.id.ref:                for s in list_line_pricelist:                    if line.product_tmpl_id.id == s.product_tmpl_id.id:                        raise ValidationError('There are frequent items')                list_line_pricelist.append(line)